package string.step2.test;

public class StringExamTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "ABC";
		System.out.print("1. Length of str : " + str.length() + "\n");
		System.out.print("2. '3rd char' --> 'D' : " + str.replace('C', 'D') + "\n");
		System.out.print("3. '2~3' : " + str.substring(1));
		
	}

}
